-- 创建菜品表dish
DROP TABLE IF EXISTS `dish`;
CREATE TABLE `dish` (
                        `id` bigint(20) NOT NULL COMMENT '主键',
                        `name` varchar(64) COLLATE utf8_bin NOT NULL COMMENT '菜品名称',
                        `category_id` bigint(20) NOT NULL COMMENT '菜品分类id',
                        `price` decimal(10,2) DEFAULT NULL COMMENT '菜品价格',
                        `image` varchar(200) COLLATE utf8_bin NOT NULL COMMENT '图片',
                        `description` varchar(400) COLLATE utf8_bin DEFAULT NULL COMMENT '描述信息',
                        `status` int(11) NOT NULL DEFAULT '1' COMMENT '0 停售 1 起售',
                        `sort` int(11) NOT NULL DEFAULT '0' COMMENT '顺序',
                        `create_time` datetime NOT NULL COMMENT '创建时间',
                        `update_time` datetime NOT NULL COMMENT '更新时间',
                        `create_user` bigint(20) NOT NULL COMMENT '创建人',
                        `update_user` bigint(20) NOT NULL COMMENT '修改人',
                        PRIMARY KEY (`id`) USING BTREE,
                        UNIQUE KEY `idx_dish_name` (`name`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_bin COMMENT='菜品管理';

INSERT INTO `dish` VALUES ('2397849739276890115', '巧克力蛋糕', '1397844263642378262', '9800', '甜点/巧克力蛋糕.jpg', '巧克力蛋糕是一种以巧克力为主制成的蛋糕，常见于生日派对及婚礼，是常见的甜品之一。', '0', '1', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('2397849739276890116', '大福', '1397844263642378262', '7000', '甜点/大福.jpg', '大福（団子），一种日本点心，也即夹心糯米团，类似放大版的汤团。用糯米包裹着内部的馅料，通常是豆沙、草莓 、地瓜等甜味材质，口感柔软爽滑。', '0', '2', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('2397849739276890117', '杯子慕斯蛋糕', '1397844263642378262', '2300', '甜点/杯子慕斯蛋糕.jpg', '慕斯与布丁一样属于甜点的一种，其性质较布丁更柔软，入口就化。', '1', '3', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('2397849739276890118', '巧克力布朗尼', '1397844263642378262', '5000', '甜点/巧克力布朗尼.jpg', '布朗尼蛋糕的质地介于蛋糕与饼干之间，它既有乳脂软糖的甜腻，又有蛋糕的松软。', '1', '4', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('2397849739276890119', '啤酒', '1397844263642378272', '500', '饮品/啤酒.jpg', '啤酒（Beer）是一种以小麦芽和大麦芽为主要原料，并加啤酒花，经过液态糊化和糖化，再经过液态发酵酿制而成的酒精饮料。', '1', '5', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('2397849739276890120', '奶茶', '1397844263642378272', '900', '饮品/奶茶.jpg', '煮沸的红茶中加入牛奶，以中和茶中的涩味。', '1', '6', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '0', '1');
INSERT INTO `dish` VALUES ('2397849739276890121', '可乐', '1397844263642378272', '500', '饮品/可乐.jpg', '可乐(Cola)，是指有甜味、含咖啡因但不含酒精的碳酸饮料。', '1', '7', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('2397849739276890122', '咖啡', '1397844263642378272', '1800', '饮品/咖啡.jpg', '咖啡(coffee)，是用经过烘焙磨粉的咖啡豆制作出来的饮料', '1', '8', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('2397849739276890127', '大荔冬枣', '1397844263642378282', '2200', '长安特色/fruits/fruit1.jpg', '全国各地的朋友吃的冬枣大多产自大荔。', '1', '13', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('2397849739276890128', '富平吊柿饼', '1397844263642378282', '1800', '长安特色/fruits/fruit2.jpg', '保持着新鲜柿子饱满红润的样子，吃起来口感更加的软糯香甜，深受大家的喜爱，而且还远销国外。', '1', '14', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('2397849739276890129', '板栗红薯', '1397844263642378282', '2400', '长安特色/fruits/fruit3.jpg', '板栗红薯粉皮黄肉，颜色很像板栗，加上粉糯的口感和板栗的香味，因此有了“板栗红薯”这个称呼。', '1', '15', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('2397849739276890130', '红香酥梨', '1397844263642378282', '790', '长安特色/fruits/fruit4.jpg', '渭南有着“中国酥梨之乡”的称呼，也是国内外公认的“优等酥梨最佳生态区”。', '1', '16', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('2397849739276890131', '油泼面', '1397844263642378282', '1100', '长安特色/mianshi/mianshi1.jpg', '油泼面是陕西传统的特色面食之一，起源于明代，有鲜香味、酸辣味、香辣味。', '1', '17', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('2397849739276890132', '臊子面', '1397844263642378282', '1000', '长安特色/mianshi/mianshi2.jpg', '它的味道以酸香为主，配料十分讲究，既好看又好吃。', '1', '18', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('2397849739276890133', '扯面', '1397844263642378282', '1200', '长安特色/mianshi/mianshi3.jpg', '扯面顾名思义就是将醒好的面条扯开，有宽面，也有韭叶。', '1', '19', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('2397849739276890134', '驴蹄子面', '1397844263642378282', '800', '长安特色/mianshi/mianshi4.jpg', '全驴蹄子面是西安周边比较有名的面条，因为形似驴蹄子，所以因此而得名。', '1', '20', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('2397849739276890135', '肉夹馍', '1397844263642378282', '800', '长安特色/snacks/snack1.jpg', '所谓肉夹馍，其实是馍里夹上肉，是腊汁肉和白吉馍两种食材合为一体而制成的美食。', '1', '21', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('2397849739276890136', '羊肉泡馍', '1397844263642378282', '2800', '长安特色/snacks/snack2.jpg', '羊肉泡馍看似简单，其实制作起来对烹饪技艺要求是很高的，烙馍、煮肉、切肉、煮馍等，每一道工序都非常严格。', '1', '22', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('2397849739276890137', '陕西凉皮', '1397844263642378282', '700', '长安特色/snacks/snack3.jpg', '陕西的凉皮根据不同的原料和不同的制作方法分为很多种类，有面皮、米皮、魔芋皮，有蒸面皮、擀面皮、烙面皮等。', '1', '23', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('2397849739276890138', '荞面饸饹', '1397844263642378282', '700', '长安特色/snacks/snack4.jpg', '是一种古老的面食，一碗不起眼的荞面饸饹需要经过和面、醒面、切团、压面等多道工序制作而成。', '1', '24', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('1397849739276890114', '白切贵妃鸡', '1397844263642378212', '7800', '粤菜/白切贵妃鸡.jpg', '著名的粤式菜肴。以贵妃鸡为原料，肉质鲜美，制作方法特别，蘸酱食用即可', '1', '0', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('1397849739276890115', '红烧乳鸽', '1397844263642378212', '9800', '粤菜/红烧乳鸽.jpg', '广东省传统名菜之一，属于粤菜系。主要材料有乳鸽，特点是皮脆、肉滑、骨嫩、多汁。', '1', '0', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('1397849739276890116', '蜜汁叉烧', '1397844263642378212', '4800', '粤菜/蜜汁叉烧.jpg', '用梅花肉做的叉烧肉，烤过之后肥肉丝会被烤融，使肉的口感更香更嫩却不腻。 ', '1', '0', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('1397849739276890117', '干锅茶树菇', '1397844263642378242', '3800', '湘菜/干锅茶树菇.jpg', '有清热，平肝，明目的功效，可以补肾，利尿，渗湿，健脾，止泻，民间常用于治疗腰酸痛，胃冷，肾炎水肿，头晕，腹痛，呕吐等症。', '1', '0', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('1397849739276890118', '湖南小炒肉', '1397844263642378242', '3800', '湘菜/湖南小炒肉.jpg', '好吃的小炒肉细嫩、有着青椒、瘦肉和豆豉的油香但是绝不腻人。做好这个菜，食材第一，火候第二，突出到酸辣、香鲜、软嫩的品味，是最能见湘菜水平的家常菜。', '1', '0', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('1397849739276890119', '双味剁椒鱼头', '1397844263642378242', '10800', '湘菜/双味剁椒鱼头.jpg', '菜品色泽红亮、味浓、肉质细嫩。肥而不腻、口感软糯、鲜辣适口。', '1', '0', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('1397849739276890120', '外婆菜', '1397844263642378242', '2800', '湘菜/外婆菜.jpg', '是难得一见的时尚湘菜，下饭拌饭极好，口感美味，品之逾香，开胃下饭。', '1', '0', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('1397849739276890121', '湘式臭桂鱼', '1397844263642378242', '7800', '湘菜/湘式臭桂鱼.jpg', '相较于常见的淡水鱼，鳜鱼的肉质特别紧实。', '1', '0', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('1397849739276890122', '黄焖鱼翅', '1397844263642378292', '28000', '宫廷特供/黄焖鱼翅.jpg', '鱼翅有弹性，金黄亮丽，口感上软糯又容易吸收；火靠鱼翅的汤是经过12个小时慢火炖制的鸡汤，既鲜又香，浓厚滑润的感觉美妙难言。黄焖鱼翅有补中益气、强筋壮骨的作用，品尝价值很高。', '1', '0', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('1397849739276890123', '清宫万福肉', '1397844263642378292', '9800', '宫廷特供/清宫万福肉.jpg', '经过蒸、煮、烹、炸、扣等多道工序后把五花肉中的肥油全部都炼了出来，使得五花肉肥而不腻，即使常吃也不会使人发胖。', '1', '0', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('1397849739276890124', '雍亲王府烧鹿筋', '1397844263642378292', '11800', '宫廷特供/雍亲王府烧鹿筋.jpg', '梅花鹿的鹿筋弹性十足，被萝卜和苹果煨制之后，非常的清鲜而无任何腥膻之气。汤依然如常的浓郁香醇。', '1', '0', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('1397849739276890125', '御用佛跳墙', '1397844263642378292', '29800', '宫廷特供/御用佛跳墙.jpg', '汤浓郁浑厚、料清雅软糯，吃完之后沁人心脾的感觉久久不能离去。', '1', '0', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('1397849739276890126', '布利尼薄饼', '1397844263642378222', '3800', '外国/俄罗斯/布利尼薄饼/blnbb.png', '布利尼薄饼，这款俄罗斯版薄饼，集中式摊煎饼和法国煎饼于一身，可咸可甜。', '1', '0', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('1397849739276890127', '俄式奥利维尔色拉', '1397844263642378222', '3800', '外国/俄罗斯/俄式奥利维尔色拉/esalwesl.png', '所需食材通常是熟马铃薯丁、胡萝卜丁，腌黄瓜丁、豌豆、洋葱、鸡蛋、鸡肉丁或火腿丁和苹果丁。', '1', '0', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('1397849739276890128', '俄式饺子', '1397844263642378222', '3800', '外国/俄罗斯/俄式饺子/esjz.png', '俄罗斯饺子Pelymeni，或称西伯利亚饺子，是一种源自乌拉尔山的俄国菜。', '1', '0', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('1397849739276890129', '红菜汤', '1397844263642378222', '2500', '外国/俄罗斯/红菜汤/hct.png', '俄罗斯人在喝红菜汤的时候通常会加入一勺酸奶增加风味。在点餐的时候要想好是否要加酸奶喔!', '1', '0', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('1397849739276890130', '法式鹅肝', '1397844263642378222', '19900', '外国/法国菜/法式鹅肝/fseg.png', '这道名菜将法国菜的浪漫推到了极至，法式鹅肝是最适合女性的一道西菜。', '1', '0', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('1397849739276890131', '法式焗蜗牛', '1397844263642378222', '19900', '外国/法国菜/法式焗蜗牛/fsjwn.png', '味香、质嫩，食后令人回味无穷，此菜为法国蜗牛菜代表作之一，也是当今最杰出的蜗牛菜之一。', '1', '0', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('1397849739276890132', '部队火锅', '1397844263642378222', '5800', '外国/韩餐/部队火锅/bdhg.png', '部队锅是韩国的一种火锅，源于朝鲜战争时期。由于物资短缺，在京畿道议政府美军基地附近的居民，食材来源多是驻韩美军废弃的食材，如火腿、午餐肉罐头等。', '1', '0', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('1397849739276890133', '韩式炸鸡', '1397844263642378222', '2000', '外国/韩餐/韩式炸鸡/hszj.png', '韩国炸鸡又叫韩式炸鸡，和台湾大鸡排炸鸡最大的区别在于烹饪方法。韩国炸鸡一般外面都加甜辣酱。', '1', '0', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('1397849739276890134', '日式拉面', '1397844263642378222', '2500', '外国/日料/日式拉面/srlm.png', '早期最普遍的是加上叉烧肉、笋子的酱油口味，但如今拉面的口味也越来越多样化。日本拉面源自中国。', '1', '0', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('1397849739276890135', '寿司', '1397844263642378222', '2900', '外国/日料/寿司/ss.png', '寿司是一种日本传统美食，日本古代时候的寿司，是用盐和米腌制的咸鱼，后经演变形成如今的寿司。', '1', '0', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('1397849739276890136', 'Balk Ekmek', '1397844263642378222', '3000', '外国/土耳其/Balk Ekmek/Balk Ekmek.png', 'Balık（鱼），Ekmek（面包），顾名思义它就是面包夹鱼了。', '1', '0', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('1397849739276890137', 'LahmacunPide', '1397844263642378222', '4800', '外国/土耳其/LahmacunPide/LahmacunPide.png', 'Lahmacun的名字源于阿拉伯语，我更愿意称之为“土耳其馅饼”，长相酷似薄底披萨。', '1', '0', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('1397849739276890138', '帕尼尼', '1397844263642378222', '1800', '外国/意大利菜/帕尼尼/pnn.png', '帕尼尼（Panini）是意大利文，是意大利一种传统三明治，用意式面包夹好馅料后，再放在专门作帕尼尼的烘烤机中加热压烤成热的三明治。', '1', '0', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('1397849739276890139', '意大利面', '1397844263642378222', '2800', '外国/意大利菜/意大利面/ydlm.png', '意大利面（Pasta），也被称为意粉，是西餐正餐中最接近中国人饮食习惯的面点。意大利面条有很多种类，每种都有自己的名称，长短有差，其空心的种类被部分汉语使用者称为通心粉。', '1', '0', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('1397849739276890140', '印度飞饼', '1397844263642378222', '2800', '外国/印度/印度飞饼/ydfb.png', '印度飞饼”是一种名小吃，是用调和好的面粉在空中用“飞”的绝技做成，它具有美味可口、浓郁香酥的特点。有10多个品种。', '1', '0', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');
INSERT INTO `dish` VALUES ('1397849739276890141', '马萨拉咖喱鸡', '1397844263642378222', '4800', '外国/印度/马萨拉咖喱鸡/mslglj.png', '马萨拉咖喱鸡是将最具印度特色的两种调料马萨拉和咖喱结合起来熬制，加入鲜嫩多汁的鸡肉，十分入味，味道是中辣带点酸味。', '1', '0', '2021-05-27 09:38:43', '2021-05-27 09:38:43', '1', '1');

-- 创建菜品口味表
DROP TABLE IF EXISTS `dish_flavor`;
CREATE TABLE `dish_flavor` (
                               `id` bigint(20) NOT NULL COMMENT '主键',
                               `dish_id` bigint(20) NOT NULL COMMENT '菜品',
                               `name` varchar(64) COLLATE utf8_bin NOT NULL COMMENT '口味名称',
                               `value` varchar(500) COLLATE utf8_bin DEFAULT NULL COMMENT '口味数据list',
                               `create_time` datetime NOT NULL COMMENT '创建时间',
                               `update_time` datetime NOT NULL COMMENT '更新时间',
                               `create_user` bigint(20) NOT NULL COMMENT '创建人',
                               `update_user` bigint(20) NOT NULL COMMENT '修改人',
                               PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_bin COMMENT='菜品口味关系表';

